phpdoc:classref:
 The ArrayAccess<Tk, Tv> interface
 ArrayAccess<Tk, Tv>






   &reftitle.intro;


    Interface to provide accessing objects as arrays.






   &reftitle.interfacesynopsis;



    ArrayAccess<Tk, Tv>




      ArrayAccess<Tk, Tv>




    &Methods;









    Basic usage


<?hh
class Obj<string, string> implements ArrayAccess<string, string> {
  private array $container = array();
  public function __construct() {
    $this->container = array(
      "one"   => 1,
      "two"   => 2,
      "three" => 3,
    );
  }
  public function offsetSet(string $key, string $value): this {
    if (is_null($key)) {
      $this->container[] = $value;
    } else {
      $this->container[$key] = $value;
    }
  }
  public function offsetExists(string $offset): bool {
    return isset($this->container[$offset]);
  }
  public function offsetUnset(string $offset): this {
    unset($this->container[$offset]);
  }
  public function offsetGet(string $offset): ?string {
    return isset($this->container[$offset]) ? $this->container[$offset] : null;
  }
}

$obj = new Obj();

var_dump(isset($obj["two"]));
var_dump($obj["two"]);
unset($obj["two"]);
var_dump(isset($obj["two"]));
$obj["two"] = "A value";
var_dump($obj["two"]);
$obj[] = 'Append 1';
$obj[] = 'Append 2';
$obj[] = 'Append 3';
print_r($obj);


